"use client";

import { useEffect, useRef, useState } from "react";

const Ingredient = ({ name, benefits, image, index }) => {
  const [isVisible, setIsVisible] = useState(false);
  const ingredientRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setTimeout(() => setIsVisible(true), index * 150);
        }
      },
      { threshold: 0.1 }
    );

    if (ingredientRef.current) {
      observer.observe(ingredientRef.current);
    }

    return () => {
      if (ingredientRef.current) {
        observer.unobserve(ingredientRef.current);
      }
    };
  }, [index]);

  return (
    <div
      ref={ingredientRef}
      className={`group flex flex-col items-center transition-all duration-700 ${
        isVisible
          ? "opacity-100 translate-y-0 scale-100"
          : "opacity-0 translate-y-8 scale-95"
      }`}
      style={{ transitionDelay: `${index * 100}ms` }}
    >
      <div className="relative mb-4 transform transition-all duration-500 group-hover:scale-110">
        {/* Outer glow ring */}
        <div className="absolute -inset-2 bg-gradient-to-r from-[#c28b7a] via-[#d4a394] to-[#c28b7a] rounded-full opacity-0 group-hover:opacity-30 blur-md transition-all duration-500 animate-pulse-gentle"></div>

        {/* Main image container */}
        <div className="relative w-24 h-24 rounded-full overflow-hidden border-2 border-[#c28b7a] group-hover:border-[#d4a394] transition-all duration-300 bg-white shadow-lg group-hover:shadow-2xl">
          <img
            src={image || "/placeholder.svg?height=100&width=100"}
            alt={name}
            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
          />

          {/* Overlay gradient */}
          <div className="absolute inset-0 bg-gradient-to-t from-[#c28b7a]/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
        </div>

        {/* Floating particles */}
        <div className="absolute -top-1 -right-1 w-3 h-3 bg-[#c28b7a] rounded-full opacity-0 group-hover:opacity-100 animate-bounce transition-all duration-300"></div>
        <div className="absolute -bottom-1 -left-1 w-2 h-2 bg-[#d4a394] rounded-full opacity-0 group-hover:opacity-100 animate-bounce-delayed transition-all duration-300"></div>
      </div>

      <div className="text-center transform transition-all duration-300 group-hover:-translate-y-1">
        <h3 className="text-[#c28b7a] font-bold text-lg mb-2 group-hover:text-[#b8806f] transition-colors duration-300">
          {name}
        </h3>
        <p className="text-gray-600 text-center text-sm leading-relaxed group-hover:text-gray-700 transition-colors duration-300 max-w-32">
          {benefits}
        </p>

        {/* Animated underline */}
        <div className="mt-2 h-0.5 bg-gradient-to-r from-[#c28b7a] to-[#d4a394] transform scale-x-0 group-hover:scale-x-100 transition-transform duration-500 origin-center"></div>
      </div>
    </div>
  );
};

